catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    function ResponseFunction(response) {
        try {
            var results = JSON.parse(response);

            // 检查错误
            if (results.error) {
                alert(results.error);
                return;
            }

            // 更新 MRVS 变量集
            if (results.success && results.data && results.data.length > 0) {
                // 设置数据到 MRVS 变量 (假设变量名是 data_set)
                g_form.setValue('data_set', JSON.stringify(results.data));

                // 可选：显示成功消息
                alert('成功导入 ' + results.data.length + ' 条记录');
            } else {
                alert('Excel 文件中未找到有效数据');
            }
        } catch (e) {
            alert('处理响应时出错: ' + e);
        }
    }

    // 调用服务器端脚本
    if (newValue) {
        var ga = new GlideAjax('readEXCEL'); 
        ga.addParam('sysparm_name', 'getData'); 
        ga.addParam('sysparm_id', newValue); 
        ga.getXMLAnswer(ResponseFunction); 
    }
}
